<?php//FULL QUESTION GOES THROUG THIS SURVEY BEFORE OUTPUT TO THE SCREENfunction lastParse($str) {    $patterns = array('/((?i)\[IWER:(.*?)])/s');    $replace = array('<br/><div class="alert alert-info" role="alert"><b>\2</b></div>');    $str = preg_replace($patterns, $replace, $str);    return $str;}function displayInOtherLanguage() {    global $engine;    $display = $engine->getDisplayObject();    $old = getSurveyLanguage();    setSessionParameter(SESSION_PARAM_LANGUAGE, 1);    setSessionParameter(SESSION_PARAM_NEWLANGUAGE, null);    $str = "<div id='uscic-otherlanguageview' class='uscic-wrap'><div class='container'>";    $str .= '<ul class="nav nav-tabs" role="tablist">  <li class="active"><a data-toggle="tab" role="tab" href="#english">English</a></li>  <li role="presentation" class=""><a href="#">Dutch</a></li></ul>';    $vars = $engine->getDisplayed();    $display->setLastParse(false);    $display->setShowHeader(false);    $display->setShowFooter(false);    $engine->setRedoFills(true);    $str .= "<div class='tab-content'><div id='english' class='tab-pane active'><div class='outershield'><div class='shield'></div>";    $str .= $display->showQuestion($vars, $engine->getRgid(), $engine->getTemplate());    $str .= "</div></div></div>";    $str .= "</div></div>";    $str .= "<script type='text/javascript'>";    $str .= "$(document).ready(function() {                  var bottom = $('#uscic-mainpanel').position().top+$('#uscic-mainpanel').outerHeight(true);            $('#uscic-otherlanguageview').css('position', 'absolute');            $('#uscic-otherlanguageview').css('left', $('#uscic-mainpanel').position().left);            $('#uscic-otherlanguageview').css('width', $('#uscic-mainpanel').width());                        $('#uscic-otherlanguageview').css('top', bottom+50);            });";    $str .= "</script>";    setSessionParameter(SESSION_PARAM_LANGUAGE, $old);    // do this here since we don't call lastparse    $patterns = array('/((?i)\[IWER:(.*?)])/s');    $replace = array('<br/><div class="alert alert-info" role="alert"><b>\2</b></div>');    $str = preg_replace($patterns, $replace, $str);    return $str;}/* ALWAYS TO BE PRESENT */// function to store answers in more than just the _data table// the destination table should have the same format as the _data table// this function is called in variable.php, storeAnswer if presentfunction storeAnswerExtra($queryparams, $queryvalues, $bp) {//if (Config::prepareDataQueries() == false) {//}    //else {    //}    /* global $db, $wave;      $query = 'REPLACE INTO ' . Config::dbSurveyData() . '_tracker (' . $queryparams . ', wave) VALUES (' . $queryvalues . ',?)';      //echo "<br/>" . $query;      if ($wave == null) {      // check session for preloads first, for startup and reentry!      $pd = loadvar('pd');      if ($pd != '') {      getSessionParamsPost(loadvar('pd'), 'PD');      foreach ($_SESSION['PD'] as $field => $answer) {      if (strtoupper($field) == strtoupper("followup")) {      $wave = $answer + 1;      }      }      }      // should be set by now      else {      global $engine;      $wave = $engine->getAnswer("followup") + 1;      //echo $wave;      }      }      $bp->add(MYSQL_BINDING_INTEGER, $wave);      $db->executeBoundQuery($query, $bp->get()); */}/* CUSTOM MY HOUSEHOLD */function processGrid() {    global $engine;    $grid = $engine->getAnswer("hhgrid");    if ($grid == "") {        $grid = "[]";    }    $gridentries = json_decode($grid);    $currentnumber = $engine->getAnswer("hhmembernumber");    // first time OR more members now    if (inArray($currentnumber, array(null, "")) || $currentnumber < sizeof($gridentries)) {        $currentnumber = sizeof($gridentries);    }        // check if there is data!    $data = array();    for ($u = 1; $u < 15; $u++) {        if (isset($_POST["hhGrid_hhmemberfirstname_" . $u])) {            $arr = array();            $arr[] = $_POST["hhGrid_hhmemberfirstname_" . $u];            $arr[] = $_POST["hhGrid_hhmemberlastname_" . $u];            $arr[] = $_POST["hhGrid_hhmembergender_" . $u];            $arr[] = $_POST["hhGrid_hhmemberbirthmonth_" . $u];            $arr[] = $_POST["hhGrid_hhmemberbirthday_" . $u];            $arr[] = $_POST["hhGrid_hhmemberbirthyear_" . $u];            $arr[] = $_POST["hhGrid_hhmemberrelationship_" . $u];            $data[] = implode(",", $arr);        }    }        //echo implode("~",$data);    // update hhmember number    $engine->setAnswer("griddata", implode("~",$data));    $engine->setAnswer("gridfilled", loadvar("gridfilled"));    $engine->setAnswer("hhmembernumber", sizeof($gridentries));    // update fields --> seems to work, but not showing yet --> array update perhaps!    for ($i = 1; $i <= $currentnumber; $i++) {        // we lost entries, and so we need to clear the other ones we had        if ($i > sizeof($gridentries)) {            $engine->setAnswer("hhmemberfirstname[" . $i . ']', null);            $engine->setAnswer("hhmemberlastname[" . $i . ']', null);            $engine->setAnswer("hhmemberage[" . $i . ']', null);            $engine->setAnswer("hhmemberdateofbirth_day[" . $i . ']', null);            $engine->setAnswer("hhmemberdateofbirth_month[" . $i . ']', null);            $engine->setAnswer("hhmemberdateofbirth_year[" . $i . ']', null);            $engine->setAnswer("hhmembergender[" . $i . ']', null);            $engine->setAnswer("hhmemberrelationship[" . $i . ']', null);            $engine->setAnswer("hhmemberrelationship_other[" . $i . ']', null);            $engine->setAnswer("hhmemberactive[" . $i . ']', null);        }        // entry!        else {            $entry = $gridentries[$i - 1]; // start at 0!            $engine->setAnswer("hhmemberfirstname[" . $i . ']', $entry->hhmemberfirstname);            $engine->setAnswer("hhmemberlastname[" . $i . ']', $entry->hhmemberlastname);            $engine->setAnswer("hhmemberdateofbirth_day[" . $i . ']', $entry->hhmemberbirthday);            $engine->setAnswer("hhmemberdateofbirth_month[" . $i . ']', $entry->hhmemberbirthmonth);            $engine->setAnswer("hhmemberdateofbirth_year[" . $i . ']', $entry->hhmemberbirthyear);            $age = floor((strtotime(date('Y-m-d')) - strtotime($entry->hhmemberbirthyear . '-' . $entry->hhmemberbirthmonth . '-' . $entry->hhmemberbirthday)) / 31556926);            $engine->setAnswer("hhmemberage[" . $i . ']', $age);            $engine->setAnswer("hhmembergender[" . $i . ']', $entry->hhmembergender);            $engine->setAnswer("hhmemberrelationship[" . $i . ']', $entry->hhmemberrelationship);            $engine->setAnswer("hhmemberrelationship_other[" . $i . ']', $entry->hhmemberrelationship_other);            $engine->setAnswer("hhmemberactive[" . $i . ']', 1); // active!        }    }    //print_r($engine->getAnswer("hhmembername"));}function processGridChange() {    //echo 'yyyy';    global $engine;    $grid = $engine->getAnswer("hhgrid_changed");    if ($grid == "") {        $grid = "[]";    }    $gridentries = json_decode($grid);    $currentnumber = $engine->getAnswer("hhmembernumber");    for ($i = 1; $i <= $currentnumber; $i++) {        $entry = $gridentries[$i - 1]; // start at 0!        $engine->setAnswer("hhmemberchanged[" . $i . ']', $entry->hhmemberchanged);    }}function processGridAdditions() {    global $engine;    $grid = $engine->getAnswer("hhgrid_new");    if ($grid == "") {        $grid = "[]";    }    $gridentries = json_decode($grid);        $existingnumber = $engine->getAnswer("hhmembernumber");    $newnumber = $engine->getAnswer("newhhmembernumber");    // first time OR more members now    if (inArray($newnumber, array(null, "")) || $newnumber < sizeof($gridentries)) {        $newnumber = sizeof($gridentries);    }    $data = array();    for ($u = 1; $u < 15; $u++) {        if (isset($_POST["hhGrid_hhmemberfirstname_" . $u])) {            $arr = array();            $arr[] = $_POST["hhGrid_hhmemberfirstname_" . $u];            //$arr[] = $_POST["hhGrid_hhmemberlastname_" . $u];            $arr[] = $_POST["hhGrid_hhmembergender_" . $u];            $arr[] = $_POST["hhGrid_hhmemberbirthmonth_" . $u];            //$arr[] = $_POST["hhGrid_hhmemberbirthday_" . $u];            $arr[] = $_POST["hhGrid_hhmemberbirthyear_" . $u];            $arr[] = $_POST["hhGrid_hhmemberrelationship_" . $u];            $data[] = implode(",", $arr);        }    }    $engine->setAnswer("newgriddata", implode("~",$data));    //echo implode("~",$data);    // update new hhmember number    $engine->setAnswer("newgridfilled", loadvar("newgridfilled"));        // update new hhmember number    $engine->setAnswer("newhhmembernumber", sizeof($gridentries));    // update fields --> seems to work, but not showing yet --> array update perhaps!    for ($i = 1; $i <= $newnumber; $i++) {        $storeindex = $existingnumber + $i;        // we lost entries, and so we need to clear the other ones we had        if ($i > sizeof($gridentries)) {            $engine->setAnswer("hhmemberfirstname[" . $storeindex . ']', null);            $engine->setAnswer("hhmemberlastname[" . $storeindex . ']', null);            $engine->setAnswer("hhmemberage[" . $storeindex . ']', null);            $engine->setAnswer("hhmemberdateofbirth_day[" . $storeindex . ']', null);            $engine->setAnswer("hhmemberdateofbirth_month[" . $storeindex . ']', null);            $engine->setAnswer("hhmemberdateofbirth_year[" . $storeindex . ']', null);            $engine->setAnswer("hhmembergender[" . $storeindex . ']', null);            $engine->setAnswer("hhmemberrelationship[" . $storeindex . ']', null);            $engine->setAnswer("hhmemberrelationship_other[" . $storeindex . ']', null);            $engine->setAnswer("hhmemberactive[" . $storeindex . ']', null);        }        // entry!        else {            $entry = $gridentries[$i - 1]; // start at 0!            //print_r($entry);            $engine->setAnswer("hhmemberfirstname[" . $storeindex . ']', $entry->hhmemberfirstname);            $engine->setAnswer("hhmemberlastname[" . $storeindex . ']', $entry->hhmemberlastname);            $engine->setAnswer("hhmemberdateofbirth_day[" . $storeindex . ']', $entry->hhmemberbirthday);            $engine->setAnswer("hhmemberdateofbirth_month[" . $storeindex . ']', $entry->hhmemberbirthmonth);            $engine->setAnswer("hhmemberdateofbirth_year[" . $storeindex . ']', $entry->hhmemberbirthyear);            $age = floor((strtotime(date('Y-m-d')) - strtotime($entry->hhmemberbirthyear . '-' . $entry->hhmemberbirthmonth . '-' . $entry->hhmemberbirthday)) / 31556926);            //echo $age;            $engine->setAnswer("hhmemberage[" . $storeindex . ']', $age);            $engine->setAnswer("hhmembergender[" . $storeindex . ']', $entry->hhmembergender);            $engine->setAnswer("hhmemberrelationship[" . $storeindex . ']', $entry->hhmemberrelationship);            $engine->setAnswer("hhmemberrelationship_other[" . $storeindex . ']', $entry->hhmemberrelationship_other);            $engine->setAnswer("hhmemberactive[" . $storeindex . ']', 1); // active!        }    }}function clearGrid() {    global $engine;    $currentnumber = $engine->getAnswer("hhmembernumber");    // // clear all entries    for ($i = 1; $i <= $currentnumber; $i++) {        // we clear all entries, since we said no one else        $engine->setAnswer("hhmemberfirstname[" . $i . ']', null);        $engine->setAnswer("hhmemberlastname[" . $i . ']', null);        $engine->setAnswer("hhmemberage[" . $i . ']', null);        $engine->setAnswer("hhmemberdateofbirth_day[" . $i . ']', null);        $engine->setAnswer("hhmemberdateofbirth_month[" . $i . ']', null);        $engine->setAnswer("hhmemberdateofbirth_year[" . $i . ']', null);        $engine->setAnswer("hhmembergender[" . $i . ']', null);        $engine->setAnswer("hhmemberrelationship[" . $i . ']', null);        $engine->setAnswer("hhmemberrelationship_other[" . $i . ']', null);        $engine->setAnswer("hhmemberactive[" . $i . ']', null);    }    $engine->setAnswer("hhmembernumber", null);    $engine->setAnswer("hhgrid", null);}function clearGridAdditions() {    global $engine;    $existingnumber = $engine->getAnswer("hhmembernumber");    $newnumber = $engine->getAnswer("newhhmembernumber");    // // clear all entries    for ($i = ($existingnumber + 1); $i <= ($existingnumber + $currentnumber); $i++) {        // we clear all entries, since we said no one else        $engine->setAnswer("hhmemberfirstname[" . $i . ']', null);        $engine->setAnswer("hhmemberlastname[" . $i . ']', null);        $engine->setAnswer("hhmemberage[" . $i . ']', null);        $engine->setAnswer("hhmemberdateofbirth_day[" . $i . ']', null);        $engine->setAnswer("hhmemberdateofbirth_month[" . $i . ']', null);        $engine->setAnswer("hhmemberdateofbirth_year[" . $i . ']', null);        $engine->setAnswer("hhmembergender[" . $i . ']', null);        $engine->setAnswer("hhmemberrelationship[" . $i . ']', null);        $engine->setAnswer("hhmemberrelationship_other[" . $i . ']', null);        $engine->setAnswer("hhmemberactive[" . $i . ']', null);    }    $engine->setAnswer("newhhmembernumber", null);    $engine->setAnswer("hhgrid_existing", null);    $engine->setAnswer("hhgrid_new", null);}function sendEmail($emailaddress) {    global $engine;    $rtid = $engine->getPrimaryKey();//'tgutsche@dornsife.usc.edu'    send_email('uashelp@usc.edu', 'basweerman@gmail.com', 'new email address for ' . $rtid, 'email: ' . $emailaddress);}function send_email($fromaddress, $toaddress, $subject, $message) {    $headers = 'From: ' . $fromaddress . "\r\n" .            'Reply-To: ' . $fromaddress . "\r\n" .            'Return-Path: <>' . "\r\n" .            'X-Mailer: PHP/' . phpversion() . "\r\n";    $headers .= "MIME-Version: 1.0" . "\r\n";    //$headers .= "Content-type: text/plain; charset=ISO-8859-1";        $headers .= "Content-Type: text/html; charset=ISO-8859-1\r\n";    $arr = explode(";", $toaddress);    foreach ($arr as $a) {        mail($a, $subject, $message, $headers);    }}/* $wave = null;  function storeAnswerExtra($queryparams, $queryvalues, $bp) {  global $db, $wave;  $query = 'REPLACE INTO ' . Config::dbSurveyData() . '_tracker (' . $queryparams . ', wave) VALUES (' . $queryvalues . ',?)';  //echo "<br/>" . $query;  if ($wave == null) {  // check session for preloads first, for startup and reentry!  $pd = loadvar('pd');  if ($pd != '') {  getSessionParamsPost(loadvar('pd'), 'PD');  foreach ($_SESSION['PD'] as $field => $answer) {  if (strtoupper($field) == strtoupper("followup")) {  $wave = $answer + 1;  }  }  }  // should be set by now  else {  global $engine;  $wave = $engine->getAnswer("followup") + 1;  //echo $wave;  }  }  $bp->add(MYSQL_BINDING_INTEGER, $wave);  $db->executeBoundQuery($query, $bp->get());  } *//* END CUSTOM MY HOUSEHOLD *//* myhh functions */function getHHInfoPreload() {    global $engine;    $household = new Household($engine->getPrimaryKey());    $respondents = $household->getRespondents();    $cnt = 1;    foreach ($respondents as $respondent) {        $engine->setAnswer('HHMemberName[' . $cnt . ']', $respondent->getName());        $engine->setAnswer('HHMemberPrimkey[' . $cnt . ']', $respondent->getPrimkey());        $engine->setAnswer('HHMemberAge[' . $cnt . ']', $respondent->getAge());        $engine->setAnswer('HHMemberSex[' . $cnt . ']', $respondent->getSex());        $engine->setAnswer('HHMemberPermanent[' . $cnt . ']', $respondent->getPermanent());        $cnt++;    }    $engine->setAnswer('HHmembers', sizeof($respondents));}function getIndividualInfoPreload() {    global $engine;    $respondent = new Respondent($engine->getPrimaryKey());    $household = $respondent->getHousehold();    $respondents = $household->getRespondents();    $cnt = 1;    foreach ($respondents as $respondentL) {        if ($respondentL->isPresent()) { //don't add the moved out and died            $engine->setAnswer('HHMemberName[' . $cnt . ']', $respondentL->getName());            $engine->setAnswer('HHMemberPrimkey[' . $cnt . ']', $respondentL->getPrimkey());            $engine->setAnswer('HHMemberAge[' . $cnt . ']', $respondentL->getAge());            $engine->setAnswer('HHMemberSex[' . $cnt . ']', $respondentL->getSex());            $engine->setAnswer('HHMemberPermanent[' . $cnt . ']', $respondent->getPermanent());        }        $cnt++;    }    $engine->setAnswer('HHmembers', sizeof($respondents));    $engine->setAnswer('RAge', $respondent->getAge());    $engine->setAnswer('RSex', $respondent->getSex());    $engine->setAnswer('RName', $respondent->getName());    $engine->setAnswer('RBirthMonth', $respondent->getBirthMonth());    $engine->setAnswer('RBirthYear', $respondent->getBirthYear());    $engine->setAnswer('RConsentType', $respondent->getConsentType());    $engine->setAnswer('familyR', $respondent->getFamR());    $engine->setAnswer('financialR', $respondent->getFinR());    $engine->setAnswer('selected', $respondent->getSelected());}function getVisionTestModalStart() {    return '<div class="modal fade bs-example-modal-lg" id="myModal" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" aria-hidden="true">  <div class="modal-dialog" style="width:1000px;">    <div class="modal-content">      <div class="modal-header">        <button type="button" class="close" data-dismiss="modal"><span aria-hidden="true">&times;</span><span class="sr-only">Close</span></button>      </div>      <div class="modal-body">      <table width=100%" style="height:500px" ><tr><td align=center>';}function getV